// SPDX-License-Identifier: MIT

pragma solidity ^0.8.13;

import "@openzeppelin/contracts/access/Ownable.sol";

contract Consultancy is Ownable {
    mapping(address => bool) public consumer;
    mapping(address => bool) public advisor;

    constructor(address _advisor) {
        require(!advisor[_advisor]);
        advisor[_advisor] = true;
    }

    function openchannelAsConsumer() public payable {
        require(!consumer[msg.sender]);
        consumer[msg.sender] = true;
    }

    function closeChannel(address _signer, Verify calldata verify) public view {
        require(consumer[msg.sender] == true);
        bytes32 hashed = keccak256(abi.encodePacked(verify.message));
        bytes32 prefixedHash = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32", hashed)
        );
        address signer = ecrecover(
            prefixedHash,
            verify.sig.v,
            verify.sig.r,
            verify.sig.s
        );
        if (_signer == signer) {}
    }

    struct Verify {
        string message;
        Signature sig;
    }

    struct Signature {
        bytes32 r;
        bytes32 s;
        uint8 v;
    }

    function addAdvisor(address _advisor) public onlyOwner {
        require(!advisor[_advisor]);
        advisor[_advisor] = true;
    }

    function removeAdvisor(address _advisor) public onlyOwner {
        require(advisor[_advisor] == true);
        delete advisor[_advisor];
    }
}

